$(document).ready(function(){
    $('body').css('margin-bottom', ($('footer').height() + 50) + 'px')
    $("#pid").load("/monitor/pid");
    $("#create-time").load("/monitor/create_time");
    monitor()
    setInterval("monitor()", 1000);
});
function monitor(){
    $("#cpu-percent").load("/monitor/cpu_percent");
    $("#memory-percent").load("/monitor/memory_percent");
    $("#disk-read").load("/monitor/disk_read");
    $("#disk-write").load("/monitor/disk_write");
}
